import React, { useState, useEffect } from 'react';
import apis from '../Api.js';

function Main() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState({
    name: "",
    description: "",
    releaseDate: "",
    endDate: ""
  });

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await apis.getAllTodoItems();
      setTodos(response);
      console.log(response);
    } catch (error) {
      console.error('Error fetching todo list:', error);
    }
  };

  const handleInputChange = (event) => {
    setNewTodo({
      ...newTodo,
      [event.target.name]: event.target.value
    });
  };

  const handleAddTodo = async () => {
    try {
      await apis.createTodoItem(newTodo);
      fetchTodos();
      setNewTodo({
        name: "",
        description: "",
        releaseDate: "",
        endDate: ""
      });
      console.log('Todo added successfully');
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  return (
    <div>
      <h1>Todo List</h1>
      <div>
        <label>Name:</label>
        <input type="text" name="name" value={newTodo.name} onChange={handleInputChange} />
      </div>
      <div>
        <label>Description:</label>
        <input type="text" name="description" value={newTodo.description} onChange={handleInputChange} />
      </div>
      <div>
        <label>Release Date:</label>
        <input type="text" name="releaseDate" value={newTodo.releaseDate} onChange={handleInputChange} />
      </div>
      <div>
        <label>End Date:</label>
        <input type="text" name="endDate" value={newTodo.endDate} onChange={handleInputChange} />
      </div>
      <button onClick={handleAddTodo}>Add Todo</button>
      <hr />
      <h2>Todo List</h2>
      <ul>
        {todos && todos.map(todo => (
          <li key={todo.id}>{todo.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default Main;
